# This will now get us the current price in USDC (which is very close to USD)
def get_uni_usdc_price():
    # Create pool contract
    pool = w3.eth.contract(address=UNI_USDC_POOL, abi=UNISWAP_V3_POOL_MINIMAL_ABI)
    
    # Get token order
    token0 = pool.functions.token0().call()
    token1 = pool.functions.token1().call()
    
    # Get slot0 data which contains sqrtPriceX96
    slot0 = pool.functions.slot0().call()
    sqrt_price_x96 = slot0[0]  # First element is sqrtPriceX96
    
    # Calculate price from sqrtPriceX96
    price = (sqrt_price_x96 ** 2) / (2 ** 192)
    
    # Determine price direction based on token order
    if token0.lower() == USDC_ADDRESS.lower():
        # Pool is USDC/UNI (price is UNI per USDC)
        uni_per_usdc = price * (10**6 / 10**18)  # USDC has 6 decimals, UNI has 18
        usdc_per_uni = 1 / uni_per_usdc
    else:
        # Pool is UNI/USDC (price is USDC per UNI)
        usdc_per_uni = price * (10**18 / 10**6)  # UNI has 18 decimals, USDC has 6
    
    return usdc_per_uni

# Print the current price
uni_price = get_uni_usdc_price()
print(f"Current $UNI price: ${uni_price:.2f}")

# Now to calculate how much an attacker can gain (in terms of USD), 
# we first convert the total supply from wei to $UNI
total_supply_uni = total_supply/ (10 ** 18)

# Next we consider only the percentage of the total supply which is in the common treasury
uni_in_treasury = total_supply_uni*percent_in_treasury

# Next we convert the total $UNI available within the treasury into USD
usd_in_treasury = uni_in_treasury*uni_price

print(f"With a treasury allocation of {percent_in_treasury*100:.0f}%, there is a maximum of ${usd_in_treasury:,.2f} available to the attacker")
